import React from 'react'
import { Query } from 'react-apollo'
import { Link } from 'react-router-dom'
import gql from 'graphql-tag'

const PROFILE_QUERY = gql`
query Profile($id_user:ID){
    profile(id_user: $id_user){
      user{
        id_user
        username
        user_type
        user_avatar
      }
      posts{
        id_post
        post_desc
        post_date
        post_img
      }
    }
}
`



function ProfileBody({user}){
  return(
    <div className="container">
      <div className="row">
        <div className="col-3">
          <img src={user.user_avatar} alt=".." className="rounded-circle" style={{width:'220px', display: 'block', margin: 'auto'}} />
        </div>
        <div className="col">
          <div className="d-flex flex-column my-auto" style={{marginLeft: '30px'}}>
            <span style={{fontSize: '35px'}}> { user.username } </span>
            <span style={{fontSize: '20px'}}> { user.user_type } </span>
            {/* <span style={{marginTop:'30px'}}><b>200</b> followers</span> */}
            <div className="d-flex flex-row justify-content-around" style={{marginTop: '30px'}}>
              <span><b>200</b> Posts</span>
              <span><b>200</b> Followers</span>
              <span><b>200</b> Following</span>
              <button className="btn btn-primary">Follow</button>
            </div>
          </div>
        </div>
      </div>

      <div className="row" style={{height:'200px', marginTop:'100px'}}>
        <div className="col bg-info">

        </div>
      </div>
    </div>
  )
}

export default function Profile(props) {
    let { id_user } = props.match.params

    return (
        <Query query={PROFILE_QUERY} variables={{id_user}}>
            {
                ({loading, error, data}) => {
                  if (loading) return <h1>Loading..</h1>
                  if (error) console.log(error)
                  const {user, posts} = data.profile
                  console.log(user, posts)
                  return <ProfileBody user={user}/>
                }
            }
        </Query>
    )
}
