import React, { Component, useState } from 'react'
import logo from './logo-nav.png'
import { Query } from 'react-apollo';
import gql from 'graphql-tag'

const SEARCH_QUERY = gql`
query Search ($user: String){
    search(user:$user){
      id_user
      user_avatar
      username
      user_type
    }
}
`


function List(props){
    const { user } = props
}

export class Navbar extends Component {
    constructor(props){
        super(props);
        this.state = { search: '' }
        this.handleKey = this.handleKey.bind(this)
    }

    handleKey(e){
        this.setState({ search: e.target.value})
        console.log(this.state, e.target.value)
    }

    render() {

        return (
            <nav className="fixed-top bg-dark main-hd">
                <div className="container my-1">
                    <div className="row d-flex justify-content-between">
                        <div className="col-1">
                            <img src={logo} style={{width: '40px'}}/>
                        </div>
                        <div className="col-5">
                            <input className="" style={{ width: '400px', borderRadius: '5px'}} onKeyUp={this.handleKey}/>
                            <ul className="list-group overflow-auto" style={{width:'400px', maxHeight:'300px'}}>
                                {/* <li className="list-group-item">Cras justo odio</li>
                                <li className="list-group-item">Dapibus ac facilisis in</li>
                                <li className="list-group-item">Morbi leo risus</li> */}
                                <Query query={SEARCH_QUERY} variables={{user:this.state.search}}>
                                    {
                                        ({loading, error, data}) => {
                                            if (loading) return <h1>loading..</h1>
                                            if (error) console.log(error)
                                            console.log(data)
                                            return <h1>DAT</h1>
                                        }
                                    }
                                </Query>

                            </ul>
                        </div>
                        <div className="col-3">
                            <div className="container">
                                <div className="row">
                                    <div className="col">
                                        <img src="https://img.icons8.com/material-outlined/30/000000/home--v2.png" alt=".."/>
                                    </div>
                                    <div className="col">
                                        <img src="https://img.icons8.com/material-outlined/30/000000/chat.png" alt=".."/>
                                    </div>
                                    <div className="col">
                                        <img src="https://img.icons8.com/material-outlined/30/000000/alarm.png" alt=".."/>
                                    </div>
                                    <div className="col">
                                        <img src={this.props.user_avatar} className="rounded-circle" style={{width: '30px'}} alt=".."/>
                                    </div>
                                </div>
                            </div>
                        </div>

                    </div> 


                </div>
            </nav>
        )
    }
}

export default Navbar
