import {useState} from 'react'
import gql from 'graphql-tag'
import { useQuery } from '@apollo/react-hooks'

const LOGIN_QUERY = gql`
    query Log($email: String!, $pwd: String!) {
        login(email: $email, pwd: $pwd) {
            user {
                id_user
                username
            }
            posts {
                id_post
                post_date
            }
        }
    }

`

function useInputLogin(initialValue) {
    const [value, setValue] = useState(initialValue)
    const reset = () => {
        setValue(initialValue)
    }

    const bind = {
        value,
        onChange: (e) => {
            setValue(e.target.value)
        }
    }

    const {loading, error, data} = useQuery(LOGIN_QUERY, { variables: {email:'linnf@gmail.com', pwd:'1234'} })
    console.log(data)

    return [value, bind, reset, data]
}

export default useInputLogin
