import React, { Component } from 'react'
import '../chat.css'
import emoji from './emoticonos.svg'
import { Query } from 'react-apollo'
import gql from 'graphql-tag'
import Cookie from 'js-cookie'

const CHATCONTENT_QUERY = gql`
query Chat($from: Int, $to: Int){
    conversation(from: $from, to:$to){
      id_msg
      to_id
      from_id
      date_time
      message
    }
}
`


export default function Chatcontent({conversation}) {

    return ( 
        <div className="row h-100">
            <div className="w-100 toprow" align="center">
                <h4 className="mx-auto my-auto"></h4>
            </div>

            <div className="w-100 mainrow">
                <div className="content d-flex flex-column">
                    <Query query={CHATCONTENT_QUERY} variables={{ from: parseInt(Cookie.get('user')), to: parseInt(conversation)}}>
                        {
                            ({loading, error, data}) => {
                                if (loading) return <h4>Loading..</h4>
                                if (error) console.log(error)
                                const {conversation} = data
                                console.log(conversation)
                                return conversation.slice(0).reverse().map(msg => (

                                    // <div key={msg.id_msg} style={{margin: '15px'}}><span  className="d-inline" style={{background: '#e3e3e3', padding: '10px', paddingLeft:'20px', paddingRight:'20px', borderRadius: '30px'}}>{msg.message}</span></div>
                                    <Message  key={msg.id_msg} msg={msg} />
                                ))
                            }
                        }
                    </Query>
                </div>

                <div>
                    <div className="in-msg d-flex justify-content-around">
                        <img src={emoji} width="20"/>
                        <input type="text"/>
                        <span className="text-primary">Send</span>
                    </div>
                </div>
            </div>
        </div>
    )
}


function Message({msg}){
    const user = parseInt(Cookie.get('user'))
    console.log(msg)

    if(user == msg.from_id){
        return (<div style={{margin: '15px'}}>
            <span  className="d-inline" style={{background: '#e3e3e3', padding: '10px', paddingLeft:'20px', paddingRight:'20px', borderRadius: '30px'}}>{msg.message}</span>
        </div>)
    } else {
        return (<div style={{margin: '15px'}}>
            <span  className="d-inline" style={{background: '#e3e3e3', padding: '10px', paddingLeft:'20px', paddingRight:'20px', borderRadius: '30px'}}>{msg.message}</span>
        </div>)
    }
}
