import {useState} from 'react'
import gql from 'graphql-tag'
import { useQuery } from '@apollo/react-hooks'

const LOGIN_QUERY = gql`
    query Log($email: String!, $pwd: String!) {
        login(email: $email, pwd: $pwd) {
            user {
                id_user
                username
            }
            posts {
                id_post
                post_date
            }
        }
    }

`

function useInputLogin(initE, initP) {
    let send = false
    let login = null
    const [email, setValueEmail] = useState(initE)
    const [pwd, setValuePwd] = useState(initP)

    const setSend = (value) => {
        send = value
    }
    const resetEmail = () => {
        setValueEmail(initE)
    }

    const resetPwd = () => {
        setValuePwd(initP)
    }

    const bindEmail = {
        email,
        onChange: (e) => {
            setValueEmail(e.target.value)
        }
    }

    const bindPwd = {
        pwd,
        onChange: (e) => {
            setValuePwd(e.target.value)
        }
    }

    if(send){
        const {loading, err, data} = useQuery(LOGIN_QUERY, {variables: {email, pwd}})
        console.log(data)
        login = data
    }

    return [email, pwd, setValueEmail, setValuePwd, resetEmail, resetPwd, bindEmail, bindPwd, login]
    //return [value, bind, reset, data]
}

export default useInputLogin
