import React, {useState} from 'react'
import logo from './logo.png'
import gql from 'graphql-tag'
import { useQuery } from '@apollo/react-hooks'

const LOGIN_QUERY = gql`
    query Log($email: String!, $pwd: String!) {
        login(email: $email, pwd: $pwd) {
            user {
                id_user
                username
            }
            posts {
                id_post
                post_date
            }
        }
    }

`


export default function Login() {
    const [email, setEmail] = useState('')
    const [pwd, setPwd] = useState('')

    const onSubmit = (e) => {
        e.preventDefault()
        alert(`email: ${email} pwd: ${pwd}`)

    }



    return (
        <div className="text-center">
            <form className="form-signin bg-dark mx-auto my-auto text-light" style={{width: '450px', borderRadius: '25px'}} onSubmit={onSubmit}>
                <img className="mb-4" src={logo} alt="" width="250"/>
                <h1 className="h3 mb-3 font-weight-normal">Please sign in</h1>
                <label htmlFor="inputEmail" className="sr-only">Email address</label>

                <input 
                    type="email"
                    id="inputEmail"
                    value={email}
                    onChange={e => setEmail(e.target.value)}
                    className="form-control"
                    placeholder="Email address"
                    name="email"
                    style={{width:'430px', margin: '10px'}}
                    required autoFocus/>

                <label htmlFor="inputPassword" className="sr-only">Password</label>

                <input type="password"
                    id="inputPassword"
                    value={pwd}
                    onChange={e => setPwd(e.target.value)}
                    className="form-control"
                    placeholder="Password"
                    name="pwd"
                    style={{width:'430px', margin: '10px'}} 
                    required />
                <div className="checkbox mb-3">
                    <label>
                    <input type="checkbox" value="remember-me" name="remember"/> Remember me
                    </label>
                </div>
                <button className="btn btn-lg btn-info btn-block" type="submit">Sign in</button>
                <p className="mt-5 mb-3 text-muted">&copy; 2017-2019</p>
            </form>
        </div>
    )
}
