import React, { Component } from 'react'
import logo from './logo.png'
import gql from 'graphql-tag'
import {Query, useQuery} from 'react-apollo'

const LOGIN_QUERY = gql`
    query Log($email: String!, $pwd: String!) {
        login(email: $email, pwd: $pwd) {
            user {
                id_user
                username
            }
            posts {
                id_post
                post_date
            }
        }
    }

`

export class login extends Component {
    constructor(){
        super();
        this.handleSubmit = this.handleSubmit.bind(this)
    }

    login(email, pwd) {
        let { loading, error, data } = useQuery(LOGIN_QUERY, { variables: { email, pwd } } )

        if(loading) console.log("Loading..") 
        if(error) console.error(error)

        return data
    }

    handleSubmit(e) {
        e.preventDefault();
        const email = document.getElementsByName("email")[0].value
        const pwd = document.getElementsByName("pwd")[0].value
        const checked = document.getElementsByName("remember")[0].checked
        console.log(email, pwd, checked)
        console.log(this.login(email, pwd))

    }

    render() {
        return (
            <div className="text-center">
                <form className="form-signin bg-dark mx-auto my-auto text-light" style={{width: '450px', borderRadius: '25px'}} onSubmit={this.handleSubmit}>
                    <img className="mb-4" src={logo} alt="" width="250"/>
                    <h1 className="h3 mb-3 font-weight-normal">Please sign in</h1>
                    <label htmlFor="inputEmail" className="sr-only">Email address</label>
                    <input type="email" id="inputEmail" className="form-control" placeholder="Email address" name="email" style={{width:'430px', margin: '10px'}} required autoFocus/>
                    <label htmlFor="inputPassword" className="sr-only">Password</label>
                    <input type="password" id="inputPassword" className="form-control" placeholder="Password" name="pwd" style={{width:'430px', margin: '10px'}} required />
                    <div className="checkbox mb-3">
                        <label>
                        <input type="checkbox" value="remember-me" name="remember"/> Remember me
                        </label>
                    </div>
                    <button className="btn btn-lg btn-info btn-block" type="submit">Sign in</button>
                    <p className="mt-5 mb-3 text-muted">&copy; 2017-2019</p>
                </form>
            </div>

        )
    }
}

export default login
